var userId = current.getValue('proposer'); //用户id
var duration = current.getValue('duration'); //请假天数
var type = current.getValue('holiday_type'); //假期类型
var files = current.getValue('file'); //获取文件
var reason = current.getValue('reason'); //详情理由
var startTime = current.getValue('start_time'); //开始时间
var endTime = current.getValue('end_time'); //结束时间


var holidaySurplus = new GlideRecord('x_yunji_lm_holiday_surplus');
holidaySurplus.addQuery('name', userId);

var holidayTypeGr = new GlideRecord('x_yunji_lm_holiday_type');
holidayTypeGr.addQuery('sys_id', type);
holidayTypeGr.query();
while (holidayTypeGr.hasNext()) {
  holidayTypeGr.next();
  holidaySurplus.addQuery('holiday_type', holidayTypeGr.getValue('holiday_type'));
}

// 执行
holidaySurplus.query();
while (holidaySurplus.hasNext()) {
  holidaySurplus.next();
  var complete = new GlideRecord('x_yunji_lm_holiday_complete');
  complete.setValue('name', userId);
  complete.setValue('holiday_type', type);
  complete.setValue('enclosure', files);
  complete.setValue('reason', reason);
  complete.setValue('duration', duration);
  complete.setValue('start_time', startTime);
  complete.setValue('end_time', endTime);
  complete.insert();

}

var userId = current.getValue('proposer'); //用户id
var duration = current.getValue('duration'); //请假天数
var type = current.getValue('holiday_type'); //假期类型
// var userAjax = new GlideAjax('SurplusHoliday'); //Pass in Script Include name
//   userAjax.addParam('sysparm_name', 'surplusDay'); //Method name
//   userAjax.addParam('holiday_type', type)//参数

//     var days = answer.data.days
//     var surplusDays = days - duration;
gs.print("############## duration:" + duration);
var holidaySurplus = new GlideRecord('x_yunji_lm_holiday_surplus');
holidaySurplus.addQuery('name', userId);

var holidayTypeGr = new GlideRecord('x_yunji_lm_holiday_type');
holidayTypeGr.addQuery('sys_id', type);
holidayTypeGr.query();
while (holidayTypeGr.hasNext()) {
  holidayTypeGr.next();
  holidaySurplus.addQuery('holiday_type', holidayTypeGr.getValue('holiday_type'));
}
// 执行
holidaySurplus.query();
while (holidaySurplus.hasNext()) {
  gs.print("############## 扣减假期duration:" + duration);
  holidaySurplus.next();
  holidaySurplus.setValue('surplus_days', holidaySurplus.getValue('surplus_days') - duration);
  holidaySurplus.setValue('holiday_type', holidaySurplus.getValue('holiday_type'));
  holidaySurplus.setValue('name', holidaySurplus.getValue('name'));
  holidaySurplus.update();
}

